    let function_name = fn.name;
    let key = {
        function_name,
        args
    };
    let key_json = json_to(key);
    let key_encoded = string_encoded_to(key_json);
    let file_name = file_name_json(key_encoded);
    let file_path = path_join([folder_gitignore(), 'cache', file_name]);
    let result;
    let json;
    let object;
