import {list_concat} from "./list_concat.mjs";
import {function_new_generic} from "./function_new_generic.mjs";
import {equal} from "./equal.mjs";
import {list_map} from "./list_map.mjs";
import {assert_boolean} from "./assert_boolean.mjs";
import {string_includes} from "./string_includes.mjs";
import {function_run} from "./function_run.mjs";
export async function tests_generate_single(function_name, args, test_number) {
    let result = await function_run(function_name, args);
    console.log(test_number.toString(), list_concat(args, [result]));
    let result_name = 'result';
    let string_delimeter = "'";
    for (let arg of args) {
        assert_boolean(!string_includes(arg, string_delimeter));
    }
    let args_mapped = list_map(args, arg => {
        let result = string_delimit(arg);
        return result;
    });
    await function_new_generic(`${function_name}_test_${test_number}`, ``, `    let ${result_name} = ${function_name}(${args_mapped.join(', ')});
    ${assert_boolean.name}(${equal.name}(${result_name}, ${result}))`, false, [assert_boolean.name, equal.name], false);
}

args../public/src/tuples_2.mjs:
import {string_split_comma} from "./string_split_comma.mjs";
import {function_run} from "./function_run.mjs";
import {list_map_async} from "./list_map_async.mjs";
import {assert_boolean} from "./assert_boolean.mjs";
import {equal} from "./equal.mjs";
import {list_get} from "./list_get.mjs";
import {list_adder} from "./list_adder.mjs";
import {list_length} from "./list_length.mjs";
export async function tuples_2(names_pair) {
    let args_values = string_split_comma(names_pair);
    let mapped = await list_map_async(args_values, async av => await function_run(av, []));
    assert_boolean(equal(list_length(mapped), 2));
    let m1 = list_get(mapped, 0);
    let m2 = list_get(mapped, 1);
    return list_adder(la => {
        for (let m1_i of m1) {
            for (let m2_i of m2) {
                la([m1_i, m2_i]);
            }
        }
    });
}

